// Gemini文字起こし結果から行配列を取得（すでにJSON配列形式）
const geminiOutput = $input.first().json;
const lines = Array.isArray(geminiOutput) ? geminiOutput : (geminiOutput.lines || []);

// Gemini出力はすでにline_id, content, speaker, timestamp等を含む
// チャンク分割設定
const chunkSize = 25;  // 20-30行の中央値
const overlapSize = 6;  // 前後3チャンク分（約6行）

const chunks = [];
for (let i = 0; i < lines.length; i += chunkSize) {
  const mainLines = lines.slice(i, i + chunkSize);
  const overlapBefore = i > 0 ? lines.slice(Math.max(0, i - overlapSize), i) : null;
  const overlapAfter = i + chunkSize < lines.length ? lines.slice(i + chunkSize, Math.min(lines.length, i + chunkSize + overlapSize)) : null;

  chunks.push({
    chunk_id: Math.floor(i / chunkSize) + 1,
    main_text: mainLines,
    overlap_before: overlapBefore,
    overlap_after: overlapAfter,
    line_range: [mainLines[0].line_id, mainLines[mainLines.length - 1].line_id],
    total_lines: mainLines.length
  });
}

return chunks.map(chunk => ({ json: chunk }));

// 全チャンクの整形結果を取得
const allChunks = $input.all();
const allLines = [];

// 各チャンクのmain_textのみを抽出
for (const chunk of allChunks) {
  const chunkData = chunk.json;
  if (chunkData && Array.isArray(chunkData)) {
    allLines.push(...chunkData);
  }
}

// line_idでソート
allLines.sort((a, b) => a.line_id - b.line_id);

// 重複削除（同じline_idは1つのみ保持）
const uniqueLines = Array.from(
  new Map(allLines.map(line => [line.line_id, line])).values()
);

// 統合結果を返す
return [{ json: { lines: uniqueLines, total_lines: uniqueLines.length } }];

