// Step2の議題JSONとStep1の統合JSONを取得
const step2Output = $input.first().json;
const allLines = $('Code: チャンク統合+重複除去').first().json.lines;

const agendas = step2Output.agendas;
const reconstructedAgendas = [];

for (const agenda of agendas) {
  // 該当するline_idsの文字起こしを収集
  const lines = agenda.line_ids.map(id => {
    return allLines.find(line => line.line_id === id);
  }).filter(line => line !== undefined);

  // タイムスタンプから duration を計算
  let duration = '0秒';
  if (lines.length > 0 && lines[0].start_time !== undefined && lines[lines.length - 1].end_time !== undefined) {
    const durationSeconds = Math.floor(lines[lines.length - 1].end_time - lines[0].start_time);
    const minutes = Math.floor(durationSeconds / 60);
    const seconds = durationSeconds % 60;
    duration = minutes > 0 ? `${minutes}分${seconds}秒` : `${seconds}秒`;
  }

  reconstructedAgendas.push({
    agenda_id: agenda.agenda_id,
    title: agenda.title,
    lines: lines,
    context: {
      total_lines: lines.length,
      duration: duration,
      line_ids: agenda.line_ids
    }
  });
}

// 各議題を個別のアイテムとして返す（Split in Batchesで並列処理するため）
return reconstructedAgendas.map(agenda => ({ json: agenda }));

