const items = $input.all();
const uniqueLines = new Map();

for (const item of items) {
  const formattedChunk = item.json.formatted_chunk || item.json.output || '';
  const lines = formattedChunk.split('\n').filter(line => line.trim().length > 0);

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();
    const lineId = `${item.json.chunk_id}_${i}`;

    // line_idã§ãƒ¦ãƒ‹ãƒ¼ã‚¯åŒ–ï¼ˆé‡è¤‡ã‚’æ’é™¤ï¼‰
    if (!uniqueLines.has(line)) {
      uniqueLines.set(line, {
        line_id: lineId,
        chunk_id: item.json.chunk_id,
        line_number: i + 1,
        content: line,
        speaker: line.match(/\[([^\]]+)\]/)?.[1] || 'Unknown'
      });
    }
  }
}

// è¡Œç•ªå·é †ã«ã‚½ãƒ¼ãƒˆ
const sortedLines = Array.from(uniqueLines.values())
  .sort((a, b) => {
    const [aChunk, aLine] = a.line_id.split('_').map(Number);
    const [bChunk, bLine] = b.line_id.split('_').map(Number);
    return aChunk === bChunk ? aLine - bLine : aChunk - bChunk;
  });

return [{
  json: {
    full_transcript: sortedLines.map(l => l.content).join('\n'),
    total_lines: sortedLines.length,
    lines: sortedLines,
    speakers: [...new Set(sortedLines.map(l => l.speaker))]
  }
}];
