// ãƒ•ã‚¡ã‚¤ãƒ«åæ­£è¦åŒ–ã¨ã‚¿ã‚¤ãƒ ã‚¹ã‚¿ãƒ³ãƒ—ãƒ‘ãƒ¼ã‚¹
const items = $input.all();
const normalizedItems = [];

for (const item of items) {
  const meetingName = item.json.meeting_name;
  const timestamp = item.json.timestamp;

  // ãƒ•ã‚¡ã‚¤ãƒ«åã‹ã‚‰æ—¥æ™‚æƒ…å ±ã‚’æŠ½å‡ºï¼ˆä¾‹: "2024-01-15_14-30_å®šä¾‹ä¼šè­°.m4a"ï¼‰
  const datePattern = /(\d{4})-(\d{2})-(\d{2})_(\d{2})-(\d{2})/;
  const match = meetingName.match(datePattern);

  let parsedDate = null;
  let cleanMeetingName = meetingName;

  if (match) {
    const [_, year, month, day, hour, minute] = match;
    parsedDate = `${year}-${month}-${day}T${hour}:${minute}:00Z`;
    cleanMeetingName = meetingName.replace(datePattern, '').trim().replace(/^_+|_+$/g, '');
  }

  normalizedItems.push({
    json: {
      ...item.json,
      meeting_name: cleanMeetingName,
      meeting_date: parsedDate || timestamp,
      original_filename: meetingName
    },
    binary: item.binary
  });
}

return normalizedItems;

// æ–‡å­—èµ·ã“ã—ãƒ†ã‚­ã‚¹ãƒˆã‚’ãƒãƒ£ãƒ³ã‚¯åˆ†å‰²ï¼ˆ25è¡Œ + ã‚ªãƒ¼ãƒãƒ¼ãƒ©ãƒƒãƒ—6è¡Œï¼‰
const items = $input.all();
const chunks = [];

for (const item of items) {
  const transcript = item.json.transcript;
  const segments = item.json.segments || [];
  const lines = transcript.split('\n').filter(line => line.trim().length > 0);

  const CHUNK_SIZE = 25;
  const OVERLAP = 6;
  const chunkCount = Math.ceil(lines.length / (CHUNK_SIZE - OVERLAP));

  for (let i = 0; i < chunkCount; i++) {
    const startIndex = i * (CHUNK_SIZE - OVERLAP);
    const endIndex = Math.min(startIndex + CHUNK_SIZE, lines.length);
    const chunkLines = lines.slice(startIndex, endIndex);

    // è©²å½“ã‚»ã‚°ãƒ¡ãƒ³ãƒˆæƒ…å ±ã‚’æŠ½å‡º
    const chunkSegments = segments.filter(seg => {
      const segmentText = seg.text || '';
      return chunkLines.some(line => line.includes(segmentText));
    });

    chunks.push({
      json: {
        ...item.json,
        chunk_id: i + 1,
        chunk_total: chunkCount,
        chunk: chunkLines.join('\n'),
        chunk_lines: chunkLines.length,
        chunk_start_line: startIndex + 1,
        chunk_end_line: endIndex,
        chunk_segments: chunkSegments,
        has_overlap: i > 0
      }
    });
  }
}

return chunks;

// ãƒãƒ£ãƒ³ã‚¯ã‚ªãƒ¼ãƒãƒ¼ãƒ©ãƒƒãƒ—ã«ã‚ˆã‚‹é‡è¤‡è¡Œã‚’é™¤å»
const items = $input.all();
const uniqueLines = new Map();

for (const item of items) {
  const formattedChunk = item.json.formatted_chunk || item.json.output || '';
  const lines = formattedChunk.split('\n').filter(line => line.trim().length > 0);

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();
    const lineId = `${item.json.chunk_id}_${i}`;

    // line_idã§ãƒ¦ãƒ‹ãƒ¼ã‚¯åŒ–ï¼ˆé‡è¤‡ã‚’æ’é™¤ï¼‰
    if (!uniqueLines.has(line)) {
      uniqueLines.set(line, {
        line_id: lineId,
        chunk_id: item.json.chunk_id,
        line_number: i + 1,
        content: line,
        speaker: line.match(/\[([^\]]+)\]/)?.[1] || 'Unknown'
      });
    }
  }
}

// è¡Œç•ªå·é †ã«ã‚½ãƒ¼ãƒˆ
const sortedLines = Array.from(uniqueLines.values())
  .sort((a, b) => {
    const [aChunk, aLine] = a.line_id.split('_').map(Number);
    const [bChunk, bLine] = b.line_id.split('_').map(Number);
    return aChunk === bChunk ? aLine - bLine : aChunk - bChunk;
  });

return [{
  json: {
    full_transcript: sortedLines.map(l => l.content).join('\n'),
    total_lines: sortedLines.length,
    lines: sortedLines,
    speakers: [...new Set(sortedLines.map(l => l.speaker))]
  }
}];

