const overlapSize = 6;  // å‰å¾Œ6è¡Œã‚ªãƒ¼ãƒãƒ¼ãƒ©ãƒƒãƒ—

const chunks = [];

for (let i = 0; i < lines.length; i += chunkSize) {
  chunks.push({
    chunk_id: Math.floor(i / chunkSize) + 1,
    main_text: lines.slice(i, i + chunkSize),
