// エラー情報を取得
const error = $input.first().json;

// エラー発生ノード名を取得
const errorNodeName = error.node?.name || 'Unknown Node';

// エラーメッセージを取得
const errorMessage = error.error?.message || error.message || 'Unknown Error';

// エラースタックトレースを取得（最初の5行のみ）
const errorStack = error.error?.stack || error.stack || '';
const stackLines = errorStack.split('\n').slice(0, 5).join('\n');

// 実行IDを取得
const executionId = $execution.id || 'Unknown';

// タイムスタンプを日本時間で取得
const timestamp = new Date().toLocaleString('ja-JP', {
  timeZone: 'Asia/Tokyo',
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
});

// エラーの重要度を判定
let severity = 'ERROR';
if (errorMessage.includes('timeout') || errorMessage.includes('ETIMEDOUT')) {
  severity = 'WARNING';
} else if (errorMessage.includes('validation') || errorMessage.includes('Invalid')) {
  severity = 'WARNING';
} else if (errorMessage.includes('API') || errorMessage.includes('authentication')) {
  severity = 'CRITICAL';
}

// 入力データを取得（エラー発生時のコンテキスト）
const inputData = error.itemData || {};
const userId = inputData.user_id || 'Unknown';
const messageContent = inputData.message_content || 'Unknown';
const callbackUrl = inputData.callback_url || null;

return [{
  json: {
    // エラー基本情報
    error_node_name: errorNodeName,
    error_message: errorMessage,
    error_stack: stackLines,
    severity: severity,

    // 実行コンテキスト
    execution_id: executionId,
    timestamp: timestamp,
    workflow_name: 'Discord Calendar Manager',

    // ユーザーコンテキスト
    user_id: userId,
    message_content: messageContent,
    callback_url: callbackUrl,

    // Discord通知用フォーマット済みメッセージ
    discord_message: `🚨 **エラー発生** [${severity}]\n\n` +
      `**ワークフロー**: Discord Calendar Manager\n` +
      `**エラー発生ノード**: ${errorNodeName}\n` +
      `**エラーメッセージ**: ${errorMessage}\n` +
      `**実行ID**: ${executionId}\n` +
      `**発生時刻**: ${timestamp}\n` +
      `**ユーザーID**: ${userId}\n` +
      `**メッセージ内容**: ${messageContent.substring(0, 100)}${messageContent.length > 100 ? '...' : ''}\n\n` +
      `**スタックトレース**:\n\`\`\`\n${stackLines}\n\`\`\`\n\n` +
      `管理者に報告してください。`,

    // ログ記録用詳細情報
    log_entry: {
      timestamp: timestamp,
      severity: severity,
      workflow: 'Discord Calendar Manager',
      execution_id: executionId,
      error_node: errorNodeName,
      error_message: errorMessage,
      error_stack: errorStack,
      user_id: userId,
      message_content: messageContent,
      input_data: inputData
    }
  }
}];

