parameters.jsCode:
// Initialize global error stats storage
if (!$vars.errorStats) {
  $vars.errorStats = {
    total_warnings: 0,
    total_errors: 0,
    total_critical: 0,
    last_warning: null,
    last_error: null,
    last_critical: null,
    error_by_node: {}
  };
}

const severity = $input.first().json.severity;
const errorNode = $input.first().json.error_node_name;
const timestamp = $input.first().json.timestamp;

// 重要度別カウント
if (severity === 'WARNING') {
  $vars.errorStats.total_warnings++;
  $vars.errorStats.last_warning = timestamp;
} else if (severity === 'ERROR') {
  $vars.errorStats.total_errors++;
  $vars.errorStats.last_error = timestamp;
} else if (severity === 'CRITICAL') {
  $vars.errorStats.total_critical++;
  $vars.errorStats.last_critical = timestamp;
}

// ノード別カウント
if (!$vars.errorStats.error_by_node[errorNode]) {
  $vars.errorStats.error_by_node[errorNode] = 0;
}
$vars.errorStats.error_by_node[errorNode]++;

return [{
  json: {
    ...($input.first().json),
    statistics: $vars.errorStats
  }
}];
